// Packages
import React, { useState, useEffect } from 'react'
import { Layout } from 'antd'
import 'antd/dist/antd.css'
import axios from 'axios'
import { useParams } from 'react-router-dom'

// Styles
import './index.css'
import './App.css'
import { Container } from 'postcss'

const MovieDetails = (props) => {
  // console.log(props, 'id')
  // receive id
  const { location: { state } } = props
  const { id } = state
  // const { id } = useParams()
  const [movieDetails, setMovieDetails] = useState([])
  const [baseUrl, setBaseUrl] = useState('')
  const [posterSize, setPosterSize] = useState('')
  // const { id } = movieDetails.id
  const { Header, Sider, Content, Footer } = Layout
  // const { id } = props

  const getInitConfig = () => {
    axios.get('https://api.themoviedb.org/3/configuration?api_key=c1531b875c30a4f389deb26c9dc8e1f4')
      .then((res) => {
        // fetch response
        const { data: { images: { poster_sizes, base_url } } } = res
        setBaseUrl(base_url)
        setPosterSize(poster_sizes[1])
      })
      .catch((err) => {
        console.log(err)
      })
  }

  const getMovieDetails = () => {
    axios.get(`https://api.themoviedb.org/3/movie/${id}?api_key=c1531b875c30a4f389deb26c9dc8e1f4`)
      .then((res) => {
        console.log(res, 'res')
        const { data } = res
        // update array
        setMovieDetails(data)
      })
      .catch((err) => {
        console.log(err)
      })
  }
  useEffect(() => {
    getMovieDetails()
    getInitConfig()
  }, [])

  return (
    <>
      {movieDetails && movieDetails.title &&
        <Layout style={{ padding: '20px' }}>
          <Sider style={{ borderRadius: '15px', height: '100%' }}>{<img src={baseUrl + posterSize + movieDetails.poster_path} />}</Sider>
          <Layout>
            <Header style={{ backgroundColor: '#fafafa' }}>
              <h1 style={{ textAlign: 'left' }}>{movieDetails.title}</h1>
            </Header>
            <Content style={{ textAlign: 'left' }}>
              <Container>
                <div>
                  <h2>Overview</h2>
                  <p style={{ textAlign: 'left', padding: '20px' }}>
                    {movieDetails.overview}
                  </p>
                </div>
              </Container>

            </Content>
            <Footer>{movieDetails.release_date}</Footer>
          </Layout>
        </Layout>}

      {/* <Descriptions title='Movie Details'>
        <Descriptions.Item label='Movie Title'>{movieDetails.original_title}</Descriptions.Item>
      </Descriptions>
      <Descriptions>
        <Descriptions.Item label='Overview'>{movieDetails.overview}</Descriptions.Item>
      </Descriptions>
      <Descriptions>
        <Descriptions.Item label='Release Date'>{movieDetails.release_date}</Descriptions.Item>
      </Descriptions> */}
    </>
  )
}

export default MovieDetails
