// Packages
import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { Row, Col, Card, Empty, Input } from 'antd'
import { useHistory } from 'react-router-dom'

// Components
import Search from './Search'
// import MovieDetails from './MovieDetails'

const PopularMovies = () => {
  const history = useHistory()
  const [baseUrl, setBaseUrl] = useState('')
  const [posterSize, setPosterSize] = useState('')
  const [popularData, setPopularData] = useState([])
  const [isPopularMovies, setPopularMovies] = useState(false)
  const [searchValue, setSearchValue] = useState('')
  const [searchResults, setSearchResults] = useState([])

  // const { Header, Sider, Content, Footer } = Layout
  const { Search } = Input

  const getInitConfig = () => {
    axios.get('https://api.themoviedb.org/3/configuration?api_key=c1531b875c30a4f389deb26c9dc8e1f4')
      .then((res) => {
        // fetch response
        const { data: { images: { poster_sizes, base_url } } } = res
        setBaseUrl(base_url)
        setPosterSize(poster_sizes[0])
      })
      .catch((err) => {
        console.log(err)
      })
  }

  const getPopular = () => {
    setPopularMovies(true)
    axios.get('https://api.themoviedb.org/3/movie/popular?api_key=c1531b875c30a4f389deb26c9dc8e1f4&language=en-US&page=1')
      .then((res) => {
        // console.log(res)
        const { data: { results } } = res
        // update array
        setPopularData(results)
      })
      .catch((err) => {
        console.log(err)
      })
  }
  const handleOnChange = e => {
    console.log(e.target.value)

  }
  const handleSearch = (searchValueInput) => {
    axios.get('https://api.themoviedb.org/3/search/movie?api_key=c1531b875c30a4f389deb26c9dc8e1f4&language=en-US&query=%27%27&page=1&include_adult=false', {
      params: {
        query: searchValueInput
      }
    })
      .then((res) => {
        // fetch response
        const { data: { results } } = res
        // Assign to setSearchResults - setSearchResults(data)
        setSearchResults(results)
      })
      .catch((err) => {
        console.log(err)
      })
    // .finally(setSearchValue('')) // clear search values)
    setSearchValue('')
  }

  useEffect(() => {
    getInitConfig()
    getPopular()
  }, [])

  // const handleSearchInput = (input) => {
  //   console.log(input, 'input')
  //   if (input) {
  //     setSearchInput(input)
  //   } else {
  //     setSearchInput('')
  //   }
  // }
  return (
    <>
      <div className='jumbotron' style={{ margin: '1rem' }}>
        <h1>Popular Movies</h1>
        <Row style={{ backgroundColor: 'grey' }}>
          <Col>
            <Search
              placeholder='Search For Popular Movies'
              // set value
              value={searchValue}
              // update func to new value
              onChange={handleOnChange}
              onSearch={handleSearch}
              style={{ width: 300 }}
              enterButton='Search'
            />
          </Col>
        </Row>
        {/* <Input /> */}
        <br />
      </div>
      <div>
        {isPopularMovies && (
          <Row gutter={[16, 32]}>
            {/* {popularData.length ? popularData.map(({ id, title, poster_path, release_date, overview }, i) => { */}
            {popularData.length ? popularData.map((movieData, i) => {
              console.log(movieData, 'md')
              const { id, title, poster_path } = movieData
              return (
                <Col>
                  {/* <Link to='/MovieDetails'> */}
                    <Card
                      key={i}
                      size='small'
                      style={{ width: 200, height: '100%', borderRadius: '20px' }}
                      cover={<img src={baseUrl + posterSize + poster_path} alt={i} />}
                      onClick={() => console.log(id)}
                      hoverable
                    >
                      <h4 style={{ textAlign: 'left' }}>{title}</h4>
                    </Card>
                  {/* </Link> */}
                </Col>
              )
            })
              : <Empty />}
          </Row>
        )}

      </div>
    </>
  )
}

export default PopularMovies
