import React, { useEffect } from 'react'
import axios from 'axios'

const TrendingMovies = () => {

  const getInitConfig = () => {
    axios.get('https://api.themoviedb.org/3/configuration?api_key=c1531b875c30a4f389deb26c9dc8e1f4')
      .then((res) => {
        // fetch response
        const { data: { images: { poster_sizes, base_url } } } = res
        setBaseUrl(base_url)
        setPosterSize(poster_sizes[0])
      })
      .catch((err) => {
        console.log(err)
      })
  }

  const geTrendingMovies = () => {
    // setPopularMovies(true)

    // fetch all trending Movies]
    axios.get(`https://api.themoviedb.org/3/trending/all/day?api_key=${process.env.API_KEY}`)
      .then((res) => {
        // console.log(res)
        const { data } = res
        console.log(data, 'data')
        // update array
        // setPopularData(results)
      })
      .catch((err) => {
        console.log(err)
      })
  }

  useEffect(() => {
    geTrendingMovies()
    getInitConfig()
  }, [])
  return (
    <>
      Trending
    </>
  )
}

export default TrendingMovies
